import React, { useState } from "react";
import "./App.css";
import { Link, Navigate, Route, Routes, useNavigate } from "react-router-dom";
import Home from "./components/Home";
import Register from "./components/Register";
import Login from "./components/Login";
import Account from "./components/Account";
import Products from "./components/Product";
import Categories from "./components/Categories";
import Category from "./components/Category";
import CartItems from "./components/CartItems";
import NotFound from "./components/NotFound";
import CategoryLayout from "./components/CategoryLayout";

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  /* const navigate = useNavigate(); */

  return (
    <>
      <nav>
        <ul>
          <li>
            <Link to="/">Home</Link>
          </li>
          <li>
            <Link to="/register">Register</Link>
          </li>
          <li>
            <Link to="/login">Login</Link>
          </li>
          <li>
            <Link to="/account">Account</Link>
          </li>
          <li>
            <Link to="/categories">Categories</Link>
          </li>
          <li>
            <Link to="/products">Products</Link>
          </li>
          <li>
            <Link to="/cart_items">Cart</Link>
          </li>
        </ul>
      </nav>
      <Routes>
        <Route path="/" element={<Home />} />

        <Route path="/register" element={ !isAuthenticated ? <Register /> : <Navigate to='/login' /> } />
        <Route path="/login" element={ !isAuthenticated ? <Register /> : <Navigate to='/login' /> } />
        <Route path="/account" element={ !isAuthenticated ? <Register /> : <Navigate to='/login' /> } />
        <Route path="/login" element={<Login />} />
        <Route path="/account" element={<Account />} />

        <Route path="/categories" element={<CategoryLayout />}>
          <Route index element={<Categories />} />
          <Route path=":name" element={<Category />} />
        </Route>
        <Route path="/products" element={<Products />} />
        <Route path="/cart_items" element={<CartItems />} />
        <Route path="*" element={<NotFound />} />
      </Routes>
    </>
  );
}

export default App;
