import { baseUrl } from "./baseUrl";

export const getUserAddress = async (id) => {
  try {
    const response = await fetch(
      `${baseUrl}/user_addresses?user_id=${id}`
    );
    const data = await response.json();
    return data
  } catch (err) {
    console.error(err.message);
  }
}
