import React, { useEffect } from "react";
import axios from "axios";
import { baseUrl } from "../utils/API";
import { useRecoilState, useRecoilValue } from "recoil";
import { userAtom } from "../recoil/user/atom";
import { cartItemsAtom } from "../recoil/cartItems/atom";

export default function CartItems() {
  const [cartItems, setCartItems] = useRecoilState(cartItemsAtom);
  const user = useRecoilValue(userAtom);

  const {id} = user
  //Sending the cart to the db on every cart change
  useEffect(() => {
    const postData = async () => {
      const body = {product_id: cartItems.product_id, quantity: cartItems.quantity};
      await axios.post(`${baseUrl}/cart_items/${userId}`, {
        body: body,
      });
    }
    postData();
  }, [cartItems, userId]);

  //retrieving the cart from db 
  useEffect(() => {
    const fetchData = async () => {
      const result = await axios.get(`${baseUrl}/cart_items/${userId}`);
      setCartItems(result.data);
    };
    fetchData();
  }, [userId, setCartItems]);

  return (
    <div>
      <h2>CartItems</h2>
      <ul>
        {cartItems.map((cartItem) => (
          <li>
            Id {cartItem.id} Quantity: {cartItem.quantity}
          </li>
        ))}
      </ul>
    </div>
  );
}
