import React, { useState, useEffect } from "react";
import axios from "axios";
import { baseUrl } from "../utils/API";
import { useRecoilState, useRecoilValue } from "recoil";
import { userAtom } from "../recoil/user/atom";

export default function CartItems() {
  const [cartItems, setCartItems] = useRecoilState();
  const userId = useRecoilValue(userAtom.id)

  useEffect(() => {
    const fetchData = async () => {
      const result = await axios.put(`${baseUrl}/cart_items/`)
    }
  })

/*   useEffect(() => {
    const fetchData = async () => {
      const result = await axios.get(`${baseUrl}/cart_items`, {
        headers: {
          "Content-Type": "application/json",
        },
        body: {
          user_id: 1,
        },
      });
      setCartItems(result.data);
    };
    fetchData();
  }, []); */

  return (
    <div>
      <h2>CartItems</h2>
      <ul>
        {cartItems.map((cartItem) => (
          <li>
            Name: {cartItem.name} Quantity: {cartItem.quantity}
          </li>
        ))}
      </ul>
    </div>
  );
}
