import React, { Fragment, useState } from "react";
import "./App.css";
import {
  createBrowserRouter as Router,
  Route,
  Routes,
  Link,
  useNavigate,
} from "react-router-dom";
import Products from "./components/Product";
import Categories from "./components/Category";
import CartItems from "./components/CartItems";
import Account from "./components/Account";
import Register from "./components/Register";
import Login from "./components/Login";

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const navigate = useNavigate();

  return (
    <Fragment>
      <Router>
        <main>
          <nav>
            <ul>
              <li>
                <Link to="/">Home</Link>
              </li>
              <li>
                <Link to="/auth/register">Register</Link>
              </li>
              <li>
                <Link to="/auth/login">Login</Link>
              </li>
              <li>
                <Link to="/account">Account</Link>
              </li>
              <li>
                <Link to="/products">Products</Link>
              </li>
              <li>
                <Link to="/categories">Categories</Link>
              </li>
              <li>
                <Link to="/cart_items">Cart</Link>
              </li>
            </ul>
          </nav>
          <Routes>
            <Route
              exact
              path="/auth/register"
              element={(props) => {
                !isAuthenticated ? <Register {...props} /> : navigate("/login");
              }}
            />
            <Route
              exact
              path="/auth/login"
              element={(props) => {
                !isAuthenticated ? <Login {...props} /> : navigate("/account");
              }}
            />
            <Route
              exact
              path="/account"
              element={(props) => {
                isAuthenticated ? <Account {...props} /> : navigate("/login");
              }}
            />
            <Route exact path="/products" element={<Products />} />
            <Route exact path="/categories" element={<Categories />} />
            <Route exact path="/cart_items" element={<CartItems />} />
          </Routes>
        </main>
      </Router>
    </Fragment>
  );
}

export default App;
