import React, { useEffect } from "react";
import { useRecoilValue } from "recoil";
import { payment } from "../api/checkout";
import { cartItemsAtom } from "../recoil/cartItems/atom";

export default function Checkout() {

  const cartItems = useRecoilValue(cartItemsAtom)

  const processPayment = async () => {
    const data = await payment(cartItems);
    console.log(data);
    const url = data.url
    console.log(url);
    window.location.href = url; 
  }

  return (
    <>
      <h4 className="my-3">Checkout </h4>
      <button className="btn btn-success" onClick={processPayment}>Pay</button>
    </>
  )
}
