  const [quantity, setQuantity] = useState(1);

  const increment = () => {
    setQuantity(quantity + 1)
  }
  const decrement = () => {
    setQuantity(quantity - 1)
  }

  return (
    <>
      <div className="card">
        <h4 className="card-header text-center">{name}</h4>
        <img
          className="card-img-top img-fluid"
          style={{ width: "200px" }}
          src={require("./apple.png")}
          alt={name}
        />
        <div className="card-body">
          <span className="card-text">{description}</span>
          <h5 className="card-text">Price: {price}</h5>
          <h6 className="card-text">Only {stock} remaining!</h6>
          <p>{sku}</p>
        </div>
        <div className="card-footer">
        <form>
          <div class="input-group">
            <div class="input-group-prepend">
              <button class="btn btn-outline-secondary" type="button" onClick={de
              }>-</button>
            </div>
            <input type="number" class="form-control" step="1" value={quantity} />
            <div class="input-group-append">
              <button class="btn btn-outline-secondary" type="button" >+</button>
              <button class="btn btn-primary" type="button">Add to cart</button>
            </div>
          </div>
        </form>
        </div>
      </div>
    </>
  );
}
