import React, { useEffect, useState } from "react";
import { toast } from "react-toastify";

export default function Account({ setAuth }) {
  const [user, setUser] = useState({});
  const [userAddress, setUserAddress] = useState({});

  console.log(user)

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch("http://localhost:5000/account", {
          method: "GET",
          headers: { token: localStorage.token }, //we send the token in the header, back-end middleware receives req.header("token")
        });
        const parsRes = await response.json();
        setUser({
          id: parsRes.id,
          first_name: parsRes.first_name,
          last_name: parsRes.last_name,
          email: parsRes.email,
          password: parsRes.password,
        });
      } catch (err) {
        console.error(err.message);
      }
    };
    fetchData();
  }, []);

  useEffect(() => {
    if (user.id) {
      const fetchData = async () => {
        try {
          const response = await fetch(
            `http://localhost:5000/user_addresses?user_id=${user.id}`
          );
          const parsRes = await response.json();
          setUserAddress({
            id: parsRes.id,
            user_id: parsRes.user_id,
            address_line1: parsRes.address_line1,
            address_line2: parsRes.address_line2,
            city: parsRes.city,
            postal_code: parsRes.postal_code,
            country: parsRes.country,
          });
        } catch (err) {
          console.error(err.message);
        }
      };
      fetchData();
    }
  }, []);

  const onChange = (e) => {
    setUser({
      ...user,
      [e.target.name]: e.target.value
    });
    setUserAddress({
      ...userAddress,
      [e.target.name]: e.target.value
    })
  };

  const submitForm = () => {};

  const logout = (e) => {
    e.preventDefault();
    localStorage.removeItem("token");
    setAuth(false);
    toast.success("Logout successfully");
  };

  return (
    <>
      <div className="container m-5">
        <form>
          <h4 className="border-bottom">Personnal information</h4>
          <div className="form-group row my-3">
            <label className="col-sm-2 col-form-label">First Name</label>
            <div class="col-sm-4">
              <input
                type="text"
                name="first_name"
                value={user.first_name}
                className="form-control"
                onChange={(e) => onChange(e)}
              />
            </div>
          </div>
          <div className="form-group row my-3">
            <label className="col-sm-2 col-form-label">Last Name</label>
            <div class="col-sm-4">
              <input
                type="text"
                value={user.last_name}
                className="form-control"
              />
            </div>
          </div>
          <div className="form-group row my-3">
            <label className="col-sm-2 col-form-label">Email</label>
            <div class="col-sm-4">
              <input
                type="text"
                value={user.email}
                className="form-control"
                disabled
              />
            </div>
          </div>
          <button
            className="col-sm-1 btn btn-outline-primary mb-3"
            onSubmit={submitForm}
          >
            Submit
          </button>

          <h4 className="border-bottom">Delivery Information</h4>
          <div className="form-group row my-3">
            <label className="col-sm-2 col-form-label">Address Line 1</label>
            <div class="col-sm-4">
              <input
                type="text"
                value={userAddress.address_line1}
                className="form-control"
              />
            </div>
          </div>
          <div className="form-group row my-3">
            <label className="col-sm-2 col-form-label">Address Line 2</label>
            <div class="col-sm-4">
              <input
                type="text"
                value={userAddress.address_line2}
                className="form-control"
              />
            </div>
          </div>
          <div className="form-group row my-3">
            <label className="col-sm-2 col-form-label">City</label>
            <div class="col-sm-4">
              <input
                type="text"
                value={userAddress.city}
                className="form-control"
              />
            </div>
          </div>
          <div className="form-group row my-3">
            <label className="col-sm-2 col-form-label">Postal Code</label>
            <div class="col-sm-4">
              <input
                type="text"
                value={userAddress.postal_code}
                className="form-control"
              />
            </div>
          </div>
          <div className="form-group row my-3">
            <label className="col-sm-2 col-form-label">Country</label>
            <div class="col-sm-4">
              <input
                type="text"
                value={userAddress.country}
                className="form-control"
              />
            </div>
          </div>
          <button className="col-sm-1 btn btn-outline-primary mb-3">
            Submit
          </button>

          <h4 className="border-bottom">Edit Password</h4>
          <div className="form-group row my-3">
            <label className="col-sm-2 col-form-label">Current Password</label>
            <div class="col-sm-2">
              <input type="text" value="" className="form-control" />
            </div>
          </div>
          <div className="form-group row my-3">
            <label className="col-sm-2 col-form-label">New Password</label>
            <div class="col-sm-2">
              <input type="text" value="" className="form-control" />
            </div>
          </div>
          <div className="form-group row my-3">
            <label className="col-sm-2 col-form-label">Confirm Password</label>
            <div class="col-sm-2">
              <input type="text" value="" className="form-control" />
            </div>
          </div>
          <button className="col-sm-1 btn btn-outline-primary mb-3">
            Submit
          </button>
        </form>
        <button className="btn btn-danger" onClick={(e) => logout(e)}>
          Logout
        </button>
      </div>
    </>
  );
}
