import { baseUrl } from "./baseUrl";

export const verifyAuth = async (token) => {
  try {
    const response = await fetch(`${baseUrl}/auth/verify`, {
      method: "GET",
      headers: { token: token },
    });
    const parsRes = await response.json();
    return parsRes;
  } catch (err) {
    console.error(err.message);
  }
};
