import React, { useEffect, useState } from "react";
import { useRecoilValue } from "recoil";
import { getOrderItems } from "../api/orders";
import { productsAtom } from "../recoil/products/atom";

export default function OrderDetails({ orderId }) {
  const [orderItems, setOrderItems] = useState([]);
  const products = useRecoilValue(productsAtom);

  useEffect(() => {
    const fetchOrderItems = async () => {
      const result = await getOrderItems(orderId);
      setOrderItems(result);
    };
    fetchOrderItems();
  }, [orderId]);

  const renderOrderItems = () => {
    orderItems.map((item) => {
      const product = products.find((p) => p.id === item.product_id);
      return (
        <tr >
          <td>{product.name}</td>
          <td>${product.price}</td>
          <td>{product.price}</td>
          <td>${item.quantity * product.price}</td>
        </tr>
      );
    });
  }



  /*  created_at
  : 
  "2023-03-06T08:12:47.915Z"
  modified_at
  : 
  "2023-03-06T08:12:47.915Z"
  order_id
  : 
  "46f92989-dc52-4eff-85a0-f263870ee120"
  product_id
  : 
  "55778b15-fe09-461c-b960-7962725369da"
  quantity
  : 
  10 */
  return (
    <div
      class="modal fade"
      id={`orderDetailsModal${orderId}`}
      tabindex="-1"
      aria-labelledby="orderDetailsModal"
      aria-hidden="true"
    >
      <div class="modal-dialog">
        <div class="modal-content">
          <div class="modal-header">
            <h5 class="modal-title" id="orderDetailsModal">
              Modal title
            </h5>
            <button
              type="button"
              class="btn-close"
              data-bs-dismiss="modal"
              aria-label="Close"
            ></button>
          </div>
          <div class="modal-body">
            <ul>
              <li>{orderItems[0]}</li>
            </ul>
          </div>
          <div class="modal-footer">
            <button
              type="button"
              class="btn btn-secondary"
              data-bs-dismiss="modal"
            >
              Close
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
