import React, { Fragment, useState } from "react";
import "./App.css";
import {
  createBrowserRouter,
  createRoutesFromElements,
  Route,
  Link,
  useNavigate,
} from "react-router-dom";
import Products from "./components/Product";
import Categories from "./components/Category";
import CartItems from "./components/CartItems";
import Account from "./components/Account";
import Register from "./components/Register";
import Login from "./components/Login";

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const navigate = useNavigate();

  const router = createBrowserRouter(createRoutesFromElements(
    <Route path="/" element={<Root />}>
      <Route
        path="/auth/register"
        element={(props) => {
          return !isAuthenticated ? <Register {...props} /> : navigate("auth/login");
        }}
      />
      <Route

        path="/auth/login"
        element={(props) => {
          return !isAuthenticated ? <Login {...props} /> : navigate("/account");
        }}
      />
      <Route

        path="/account"
        element={(props) =>
          isAuthenticated ? <Account {...props} /> : navigate("/auth/login")
        }
      />
      <Route path="/products" element={<Products />} />
      <Route path="/categories" element={<Categories />} />
      <Route path="/cart_items" element={<CartItems />} />
    </Route>
  ))

  return (
    <Fragment>
        <main>
          <nav>
            <ul>
              <li>
                <Link to="/">Home</Link>
              </li>
              <li>
                <Link to="/auth/register">Register</Link>
              </li>
              <li>
                <Link to="/auth/login">Login</Link>
              </li>
              <li>
                <Link to="/account">Account</Link>
              </li>
              <li>
                <Link to="/products">Products</Link>
              </li>
              <li>
                <Link to="/categories">Categories</Link>
              </li>
              <li>
                <Link to="/cart_items">Cart</Link>
              </li>
            </ul>
          </nav>
        </main>
    </Fragment>
  );
}

export default App;
