import React, { useEffect } from "react";
import Product from "./Product";
import { useRecoilState, useRecoilValue } from "recoil";
import { productsAtom, searchAtom } from "../recoil/products/atom";
import { getProducts } from "../api/products";
import { useLocation } from "react-router-dom";

export default function ProductList({ category }) {
  const [products, setProducts] = useRecoilState(productsAtom);
  const location = useLocation();

  /* products.filter((product) => product.name.toLowerCase().includes(search.get('search').toLowerCase())) */

  useEffect(() => {
    const fethProducts = async () => {
      const data = await getProducts();
      if (search) {
        setProducts(data.filter((product) => product.name.includes(search)));
      } else if (category) {
        setProducts(data.filter((product) => product.category_id === category.id));
      } else {
        setProducts(data);
      }
    };
    fethProducts();
  }, [category, setProducts, search]);

  const heading = category ? `Products in ${category.name}` : "All Products";


  return (
    <>
      <div className="container">
        <h1>{heading}</h1>
        <div className="row row-cols-5 g-3">
          {products.map((product, index) => (
            <div className="col" key={index}>
              <Product product={product} />
            </div>
          ))}
        </div>
      </div>
    </>
  );
}
