import React, { useEffect, useState } from "react";
import { toast } from "react-toastify";

export default function Account({ setAuth }) {
  const [user, setUser] = useState({});
  const [userAddress, setUserAddress] = useState({});

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch("http://localhost:5000/account", {
          method: "GET",
          headers: { token: localStorage.token }, //we send the token in the header, back-end middleware receives req.header("token")
        });
        const parsRes = await response.json();
        setUser({
          id: parsRes.id,
          first_name: parsRes.first_name,
          last_name: parsRes.last_name,
          email: parsRes.email,
          password: parsRes.password,
        });
      } catch (err) {
        console.error(err.message);
      }
    };
    fetchData();
  }, []);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(`http://localhost:5000/user_addresses?user_id=${user.id}`);
        const parsRes = await response.json();
        exports.getAllUserAddresses = (req, res) => {
          const user_id = req.query.user_id;

          if (!user_id) {
            pool.query("SELECT * FROM user_addresses", (error, results) => {
              if (error) {
                throw error;
              }
              res.status(200).json(results.rows);
            });
          } else if (typeof user_id !== 'string') {
            res.status(400).json({ error: 'Invalid user ID' });
          } else {
            pool.query(
              "SELECT * FROM user_addresses WHERE user_id = $1",
              [user_id],
              (error, results) => {
                if (error) {
                  throw error;
                }
                res.status(200).json(results.rows[0]);
              }
            );
          }
        };

        setUserAddress({
          id: parsRes.id,
          user_id: parsRes.user_id,
        });
      } catch (err) {
        console.error(err.message);
      }
    };
    fetchData();
  }, [user]);

  const logout = (e) => {
    e.preventDefault();
    localStorage.removeItem("token");
    setAuth(false);
    toast.success("Logout successfully");
  };

  return (
    <>
      <div className="container">
        <h3>Welcome {user.first_name}!</h3>
        <p>First Name:</p>
        <p>Address ID: {userAddress.id}</p>
        <button className="btn btn-primary" onClick={(e) => logout(e)}>
          Logout
        </button>
      </div>
    </>
  );
}
