import React, { useState } from "react";
import { useRecoilValue } from "recoil";
import { userAtom } from "../recoil/user/atom";
import { addCartItems } from "../api/cartItems";
import { toast } from "react-toastify";

export default function Product({ product }) {
  const isAuthenticated = useRecoilValue(au)
  const { id, name, price, description, sku, stock } = product;
  const [quantity, setQuantity] = useState(1);
  const user = useRecoilValue(userAtom);

  const increment = () => {
    setQuantity((prevQuantity) => prevQuantity + 1);
  };
  const decrement = () => {
    if (quantity > 1) {
      setQuantity((prevQuantity) => prevQuantity - 1);
    }
  };

  //Sending items to the cart in DB.
  const addToCart = async (id) => {
    if (id && quantity && user.id) {
      const response = await addCartItems(id, quantity, user.id);
      if (response.status === 200) {
        toast.success(
          quantity === 1
            ? `${quantity} ${name} added to cart!`
            : `${quantity} ${name}s added to cart!`
        );
        setQuantity(1);
      } else {
        toast.error(response.data);
      }
    }
  };

  return (
    <>
      <div className="card">
        <h4 className="card-header text-center">{name}</h4>
        <img
          className="card-img-top img-fluid m-auto"
          style={{ width: "200px" }}
          src={require("./apple.png")}
          alt={name}
        />
        <div className="card-body">
          <span className="card-text">{description}</span>
          <h6 className="card-text">Only {stock} remaining!</h6>
          <h5 className="card-text">$ {price}</h5>
          <p>sku:{sku}</p>
        </div>
        <div className="card-footer">
          <form>
            <div className="input-group">
              <div className="input-group-prepend">
                <button
                  className="btn btn-outline-secondary"
                  type="button"
                  onClick={decrement}
                >
                  -
                </button>
              </div>
              <input
                type="number"
                className="form-control"
                step="1"
                value={quantity}
                onChange={(event) => setQuantity(parseInt(event.target))}
                disabled
              />
              <div className="input-group-append">
                <button
                  className="btn btn-outline-secondary"
                  type="button"
                  onClick={increment}
                >
                  +
                </button>
                <button
                  className="btn btn-primary"
                  type="button"
                  name={name}
                  onClick={() => addToCart(id)}
                >
                  Add to cart
                </button>
              </div>
            </div>
          </form>
        </div>
      </div>
    </>
  );
}
