import React, { useEffect } from "react";
import { useRecoilState, useRecoilValue } from "recoil";
import { userAtom } from "../recoil/user/atom";
import { cartItemsAtom } from "../recoil/cartItems/atom";
import { productsAtom } from "../recoil/products/atom";
import { getCartItems } from "../api/cartItems";
import { Link, redirect } from "react-router-dom";
import { userAddressAtom } from "../recoil/userAddress/atom";
import { payment } from "../api/checkout";

export default function Checkout() {
  const [cartItems, setCartItems] = useRecoilState(cartItemsAtom);
  const products = useRecoilValue(productsAtom);
  const user = useRecoilValue(userAtom);
  const userAddress = useRecoilValue(userAddressAtom);

  //retrieving the cart from db
  useEffect(() => {
    const fetchCartItems = async () => {
      const data = await getCartItems(user.id);
      setCartItems(data);
    };
    fetchCartItems();
  }, [user.id, setCartItems]);

  let total = 0;

  const renderCartItems = () => {
    if (!cartItems) {
      return <h1>Nothing in cart</h1>;
    }
    return cartItems.map((cartItem) => {
      const product = products.find((p) => p.id === cartItem.product_id);
      total = total + product.price * cartItem.quantity;
      return (
        <tr key={cartItem.id}>
          <td>{product.name}</td>
          <td>${product.price}</td>
          <td>{cartItem.quantity}</td>
          <td>${cartItem.quantity * product.price}</td>
        </tr>
      );
    });
  };

  const processPayment = async () => {
    const data = await payment(cartItems);
    console.log(data)
    /* const url = data.url
    console.log(url);
    window.location.href = url; */
  }

  return (
    <>
      <div className="container">
        <h4 className="my-3">Order summary: </h4>
        <table className="table table-striped">
          <thead className="bg-dark text-light">
            <tr>
              <th scope="col">Item</th>
              <th scope="col">Unit Price</th>
              <th scope="col">Quantity</th>
              <th scope="col">Total</th>
            </tr>
          </thead>
          <tbody>{renderCartItems()}</tbody>
          <tfoot className="bg-black text-white">
            <tr>
              <td colSpan="3" className="font-weight-bold">
                Total:{" "}
              </td>
              <td className="font-weight-bold">${total}</td>
            </tr>
          </tfoot>
        </table>
        <h4 className="border-bottom my-4">Delivery Address*:</h4>
        <form>
          <div className="form-group row my-3">
            <label className="col-sm-2 col-form-label">Address Line 1</label>
            <div className="col-sm-4">
              <input
                type="text"
                name="address_line1"
                value={userAddress.address_line1 || ""}
                className="form-control"
                disabled
              />
            </div>
          </div>
          <div className="form-group row my-3">
            <label className="col-sm-2 col-form-label">Address Line 2</label>
            <div className="col-sm-4">
              <input
                type="text"
                name="address_line2"
                value={userAddress.address_line2 || ""}
                className="form-control"
                disabled
              />
            </div>
          </div>
          <div className="form-group row my-3">
            <label className="col-sm-2 col-form-label">City</label>
            <div className="col-sm-4">
              <input
                type="text"
                name="city"
                value={userAddress.city || ""}
                className="form-control"
                disabled
              />
            </div>
          </div>
          <div className="form-group row my-3">
            <label className="col-sm-2 col-form-label">Postal Code</label>
            <div className="col-sm-4">
              <input
                type="text"
                name="postal_code"
                value={userAddress.postal_code || ""}
                className="form-control"
                disabled
              />
            </div>
          </div>
          <div className="form-group row my-3">
            <label className="col-sm-2 col-form-label">Country</label>
            <div className="col-sm-4">
              <input
                type="text"
                name="country"
                value={userAddress.country || ""}
                className="form-control"
                disabled
              />
            </div>
          </div>
          <p className="text-danger">*You can edit your address in the Account section.</p>
        </form>
        <h4 className="my-3">Go to payment </h4>
        <button className="btn btn-success" onClick={processPayment}>Pay</button>
      </div>
    </>
  );
}
