import React, { useEffect, useState } from "react";
import { toast } from "react-toastify";

export default function Account({ setAuth }) {
  const [user, setUser] = useState({});
  const [userAddress, setUserAddress] = useState({});

  console.log(user);
  console.log(userAddress);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch("http://localhost:5000/account", {
          method: "GET",
          headers: { token: localStorage.token }, //we send the token in the header, back-end middleware receives req.header("token")
        });
        const parsRes = await response.json();
        setUser({
          id: parsRes.id,
          first_name: parsRes.first_name,
          last_name: parsRes.last_name,
          email: parsRes.email,
          password: parsRes.password,
        });
      } catch (err) {
        console.error(err.message);
      }
    };
    fetchData();
  }, []);

  useEffect(() => {
    if (user.id) {
      const fetchData = async () => {
        try {
          const response = await fetch(`http://localhost:5000/user_addresses?user_id=${user.id}`);
          const parsRes = await response.json();
          setUserAddress({
            id: parsRes.id,
            user_id: parsRes.user_id,
            address_line1: parsRes.address_line1,
            address_line2: parsRes.address_line2,
            city: parsRes.city,
            postal_code: parsRes.postal_code,
            country: parsRes.country
          });
        } catch (err) {
          console.error(err.message);
        }
      };
      fetchData();
    }
  }, [user]);

  const logout = (e) => {
    e.preventDefault();
    localStorage.removeItem("token");
    setAuth(false);
    toast.success("Logout successfully");
  };

  return (
    <>
      <div className="container">
        <h3>Welcome {user.first_name}!</h3>
        <form>
          <div className="form-group row">
            <label className="col-sm-2 col-form-label">Name </label>
            <input type="text" value={user.first_name} className="form-control"/>
            <button className="btn btn-primary">Edit</button>
          </div>
        </form>
        <button className="btn btn-primary" onClick={(e) => logout(e)}>
          Logout
        </button>
      </div>
    </>
  );
}
